import React from 'react';
import { storage } from '../../config/firebase';
import { ref, uploadBytes } from 'firebase/storage';
import { View, TouchableOpacity, Text, Image, TextInput } from 'react-native';
import { useState } from 'react';

export function Estoque() {
    const [nome, setNome] = useState("")
    const [image, setImage] = useState("")
    const [previewUrl, setPreviewUrl] = useState("");

    const enviarDados = () => {
        if (image) {
            const storageRef = ref(storage, pasta + '/' + nome);

            uploadBytes(storageRef, image)
                .then((snapshot) => {
                    console.log('Uploaded a blob or file!');
                })
                .catch((erro) => {
                    console.log(erro.message);
                });
        } else {
            console.log('Nenhuma imagem selecionada.');
        }
    };

    const handleChange = (e) => {
        if (e.target.files[0]) {
            setImage(e.target.files[0]);
            setPreviewUrl(URL.createObjectURL(e.target.files[0]));
        }
    };
    
    return (
        <View>
            <TextInput onChangeText={setNome}></TextInput>
            <label htmlFor="file">Enviar arquivo</label>
            <input type='file' name='file' id='file' onChange={handleChange} style={{display: 'none'}}/>
            {previewUrl && <Image source={{ uri: previewUrl }} style={{ width: 200, height: 200 }} />}
            <TouchableOpacity onPress={enviarDados}>
                <Text>Upload</Text>
            </TouchableOpacity>
        </View>
    );
}
