import { useState } from "react";
import { View, Text, Image, TouchableOpacity } from "react-native";
import { TextInput } from 'react-native-paper';
import { styles } from "./styles";
import { db } from "../../config/firebase";
import { doc, getDoc, collection } from "firebase/firestore"
export function TelaSenha({ navigation }) {
    const [password, setPassword] = useState('');
    const [secureMode, setSecureMode] = useState(true);

    async function pegarSenha() {
        const querySnapshot = await getDoc(collection(db, 'senha'))
        console.log(querySnapshot)
    }
    

    const handleLogin = () => {
        pegarSenha()
        if (password === 'sua_senha_secreta') {
            navigation.navigate('Estoque');
        } else {
            alert('Senha incorreta. Tente novamente.');
        }
    };

    return (
        <View style={styles.container}>
            <Image style={styles.imagem}
                source={require("../../../assets/LOGO.png")}
            />
            <Text style={styles.title}>
                Controle {"\n"}de Estoque
            </Text>
            <TextInput
                label="Senha"
                value={password}
                onChangeText={setPassword}
                mode="outlined"
                secureTextEntry={secureMode}
                right={
                    <TextInput.Icon
                        icon={secureMode ? 'eye-off' : 'eye'}
                        onPress={() => setSecureMode(!secureMode)}
                    />
                }
                outlineColor="rgba(0, 0, 0, 0)"
                activeOutlineColor="rgba(20, 20, 20, 0.2)"
                textColor="#141414"
                style={styles.input} />
            <TouchableOpacity style={styles.botao} onPress={handleLogin}>
                <Text style={styles.textoButton}>
                    Entrar
                </Text>
            </TouchableOpacity>
        </View>
    );
};
