import {React, useState, useEffect } from 'react';
import { ScrollView, Image, Text, View } from 'react-native';
import { ref, listAll, getDownloadURL } from 'firebase/storage';
import { storage } from '../../config/firebase.js';
import { styles } from './styles.js'

export function mainPage() {
  const [imageList, setImageList] = useState([]);

  useEffect(() => {
    const fetchImageList = async () => {
      try {
        const storageRef = ref(storage, '/natura/'); // Substitua pelo caminho real da sua pasta

        const imageListResult = await listAll(storageRef);
        const promises = imageListResult.items.map(async (imageRef) => {
          const imageUrl = await getDownloadURL(imageRef);
          return { imageUrl };
        });

        const resolvedPromises = await Promise.all(promises);
        setImageList(resolvedPromises);
      } catch (error) {
        console.error('Error fetching image list:', error);
      }
    };

    fetchImageList();
  }, []);

  return (
    <ScrollView horizontal={true}>
      <View style={{ flexDirection: 'row' }}>
        {imageList.map((item, index) => (
          <View key={index} style={{ margin: 10 }}>
            <Image source={{ uri: item.imageUrl }} style={{ width: 148, height: 184, borderRadius: 8 }} />
			<Text style>Natura</Text>
			<Text>NomeNome</Text>
          </View>
        ))}
      </View>
    </ScrollView>
  );
};
