import { View, Text, Image } from 'react-native';
import { ScrollViewPerfumes } from '../ScrollViews/ScrollViewPerfumes';
import { ScrollViewHidratantes } from '../ScrollViews/ScrollViewHidratantes';
import { useFonts } from 'expo-font';
import { NavBar } from '../NavBar/NavBar'

export function PagProduto({route}) {
    const [fontsLoaded] = useFonts({
		'Raleway-Black': require('../../../assets/fonts/Raleway-Black.ttf'),
		'Raleway-Bold': require('../../../assets/fonts/Raleway-Bold.ttf'),
		'Raleway-Medium': require('../../../assets/fonts/Raleway-Medium.ttf'),
        'Raleway-Regular': require('../../../assets/fonts/Raleway-Regular.ttf'),
	});

    const { produto } = route.params;

    return (
        <View style={{backgroundColor: '#F9F9F9', flex: 1}}>
            <View style={{position: 'fixed', zIndex: 1, width: '100%', height: 95, backgroundColor: '#FFFFFF'}}>
                <Text style={{ fontSize: 24, fontFamily: "Raleway-Bold", top: 48, textAlign: 'center' }}>{produto.nome}</Text>
            </View>
            <View style={{flex: 1, marginTop: 95}}>
                <Image
                    style={{width: "100%", height: 419.4}}
                    source={{
                    uri: produto.imageUrl,
                    }}
                />
                <View style={{flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'space-between', alignContent: 'center', paddingHorizontal: 16, paddingTop: 12, paddingBottom: 120,}}>
                    <Text style={{fontSize: 24, fontFamily: 'Raleway-Medium', color: '#222222'}}>R$ {produto.preco}</Text>
                    <Text style={{fontSize: 13, fontFamily: 'Raleway-Regular', color: '#222222', marginTop: 10}}>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proin scelerisque diam sem, sit amet fringilla lorem tincidunt ac. Etiam et mi quis odio eleifend pharetra. Aenean dictum posuere risus, in dignissim magna molestie id. Ut sagittis est ac leo malesuada ornare. Cras congue urna sed pellentesque iaculis. Fusce a imperdiet ligula, sed elementum massa. Praesent at enim vitae lacus aliquam imperdiet ut eu nisl.</Text>
                </View>
            </View>
            <NavBar></NavBar>
        </View>
    )
}
