import { TouchableOpacity, Text } from "react-native";

export function BotaoAdd({ onPress }) {
    return (
        <TouchableOpacity style={{position: 'absolute', bottom: 100, right: 20, height: 50, backgroundColor: '#303030', borderRadius: 20, paddingHorizontal: 15, paddingBottom: 6, alignItems: 'center', justifyContent: 'center', borderWidth: 2, borderColor: '#F4C095',}} onPress={onPress}>
            <Text style={{fontSize: 24, color: '#F4C095', fontWeight: '500',}}>
                + Adicionar Produto
            </Text>
        </TouchableOpacity>
    )
}
