import React from 'react';
import { View, FlatList, Text, Image, StyleSheet } from 'react-native';
import { useFonts } from 'expo-font';
import { NavBar } from '../NavBar/NavBar'

export function Categorias() {
    const [fontsLoaded] = useFonts({
        'Raleway-Black': require('../../../assets/fonts/Raleway-Black.ttf'),
        'Raleway-Bold': require('../../../assets/fonts/Raleway-Bold.ttf'),
        'Raleway-Medium': require('../../../assets/fonts/Raleway-Medium.ttf'),
    });

    const data = [
        { id: '1', title: 'Item 1', imageUrl: 'URL_DA_IMAGEM_1' },
        { id: '2', title: 'Item 2', imageUrl: 'URL_DA_IMAGEM_2' },
        { id: '3', title: 'Item 3', imageUrl: 'URL_DA_IMAGEM_3' },
    ];

    const renderItem = ({ item }) => (
        <View style={[styles.categoryCard, styles.categoryCardLayout]}>
<View style={styles.categoryCardShadowBox} />
<Text style={styles.new}>New</Text>
<Image style={styles.image41Icon} resizeMode="cover" source="image 4.1.png" />
</View>
    );

    return (
        <View style={{ backgroundColor: '#F9F9F9', flex: 1 }}>
            <View style={{ position: 'fixed', zIndex: 1, width: '100%', height: 150, backgroundColor: '#FFFFFF' }}>
                <Text style={{ fontSize: 38, fontFamily: "Raleway-Black", top: 62, left: 20 }}>Cosm√©ticos</Text>
            </View>
            <View>
                <FlatList
                    data={data}
                    keyExtractor={(item) => item.id}
                    renderItem={renderItem}
                />
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    categoryCardLayout: {
        height: 100,
        width: 343,
        left: 0,
        position: "absolute"
    },
    categoryCardShadowBox: {
        shadowOpacity: 1,
        elevation: 25,
        shadowRadius: 25,
        shadowOffset: {
            width: 0,
            height: 1
        },
        shadowColor: "rgba(0, 0, 0, 0.08)",
        backgroundColor: "#fff",
        borderRadius: 8,
        left: "0%",
        bottom: "0%",
        right: "0%",
        top: "0%",
        height: "100%",
        position: "absolute",
        width: "100%"
    },
    new: {
        top: "39%",
        left: "6.71%",
        fontSize: 18,
        lineHeight: 22,
        fontStyle: "italic",
        fontWeight: "100",
        fontFamily: "Raleway-ThinItalic",
        color: "#222",
        textAlign: "left",
        position: "absolute"
    },
    image41Icon: {
        width: "49.85%",
        left: "50.15%",
        borderTopRightRadius: 8,
        borderBottomRightRadius: 8,
        maxWidth: "100%",
        overflow: "hidden",
        maxHeight: "100%",
        bottom: "0%",
        right: "0%",
        top: "0%",
        height: "100%",
        position: "absolute"
    },
    categoryCard: {
        top: 0
    },
    categoryCard1: {
        top: 116
    },
    categoryCard2: {
        top: 232
    },
    categoryCard3: {
        top: 348
    },
    categoryCardParent: {
        flex: 1,
        height: 448,
        width: "100%"
    }
});
