import React, { useState, useEffect } from "react";
import { View, Text, Button } from "react-native";
import { db, auth } from "../../config/firebase";
import { collection, query, onSnapshot, where, getDocs } from "firebase/firestore"
export function Perfil() {
	const [nome, setNome] = useState('')
	const [email, setEmail] = useState('')

	const atual = auth.currentUser
	useEffect(() => {
		const fetchData = async () => {
			try {
				const q = query(collection(db, 'usuarios'), where("email", "==", atual.email));
				const querySnapshot = await getDocs(q);

				let obj = {};
				querySnapshot.forEach((doc) => {
					obj = doc.data();
				});

				setNome(obj.nome);
				setEmail(obj.email);
			} catch (error) {
				console.error(error);
			}
		};

		fetchData();
	}, []);

	console.log(nome, email)

	return (
		<View style={{ backgroundColor: '#F9F9F9', flex: 1 }}>
			<View style={{ position: 'fixed', zIndex: 1, width: '100%', height: 150, backgroundColor: '#FFFFFF' }}>
				<Text style={{ fontSize: 38, fontFamily: "Raleway-Black", top: 62, left: 20 }}>Cosm√©ticos</Text>
			</View>
			<View style={{ marginTop: 150, flex: 1 }}>
				<ScrollView style={{ height: 662, paddingBottom: 83 }}>
					<Text style={{ fontSize: 34, fontfamily: "Raleway-Bold", left: 28, marginBottom: 10, fontWeight: 'bold' }}>Perfumes</Text>
					<ScrollViewPerfumes></ScrollViewPerfumes>
					<Text style={{ fontSize: 34, fontfamily: "Raleway-Bold", left: 28, marginBottom: 10, marginTop: 10, fontWeight: 'bold' }}>Hidratantes</Text>
					<ScrollViewHidratantes></ScrollViewHidratantes>
				</ScrollView>
			</View>
			<NavBar></NavBar>
		</View>
	)
}
