import {React, useState, useEffect } from 'react';
import { ScrollView, Image, Text, View } from 'react-native';
import { useFonts } from 'expo-font';
import { pegarProdutosEudora } from '../../servicos/firestore'

export function ScrollViewEudora() {
	const [fontsLoaded] = useFonts({
		'Raleway-Regular': require('../../../assets/fonts/Raleway-Regular.ttf'),
		'Raleway-Italic': require('../../../assets/fonts/Raleway-Italic.ttf'),
		'Raleway-Medium': require('../../../assets/fonts/Raleway-Medium.ttf'),
	});
	const [ProdutosEudora, setProdutosEudora] = useState([]);

	useEffect(() => {
		pegarProdutosEudora(setProdutosEudora)
	}, [])

	return (
		<ScrollView showsHorizontalScrollIndicator={false} horizontal={true}>
			<View style={{ flexDirection: 'row', left: 20 }}>
				{ProdutosEudora.map((item, index) => (
				<View key={index} style={{ margin: 10, marginRight: 17 }}>
					<Image source={{ uri: item.imageUrl }} style={{ width: 148, height: 184, borderRadius: 8, shadowColor: "rgba(211, 38, 38, 0.25)", shadowOffset: {width: 0,
				height: 4
			},
			shadowRadius: 8,
			elevation: 8,
			shadowOpacity: 1, }} />
					<Text style={{fontSize: 11, color: "#9B9B9B", fontFamily: "Raleway-Regular", marginTop: 26}}>{item.pasta}</Text>
					<Text style={{fontSize: 20, color: "#222222", fontFamily: "Raleway-Italic", marginTop: 4}}>{item.nome}</Text>
					<Text style={{fontSize: 18, color: "#DB3022", fontFamily: "Raleway-Medium", marginTop: 4}}>R$ {item.preco}</Text>
				</View>
				))}
			</View>
		</ScrollView>
	);
	};
