import { View, Text, Image } from 'react-native';
import { ScrollViewPerfumes } from '../ScrollViews/ScrollViewPerfumes';
import { ScrollViewHidratantes } from '../ScrollViews/ScrollViewHidratantes';
import { useFonts } from 'expo-font';
import { NavBar } from '../NavBar/NavBar'

export function PagProduto() {
    const [fontsLoaded] = useFonts({
		'Raleway-Black': require('../../../assets/fonts/Raleway-Black.ttf'),
		'Raleway-Bold': require('../../../assets/fonts/Raleway-Bold.ttf'),
		'Raleway-Medium': require('../../../assets/fonts/Raleway-Medium.ttf'),
        'Raleway-Regular': require('../../../assets/fonts/Raleway-Regular.ttf'),
	});

    return (
        <View style={{backgroundColor: '#F9F9F9', flex: 1}}>
            <View style={{position: 'fixed', zIndex: 1, width: '100%', height: 95, backgroundColor: '#FFFFFF'}}>
                <Text style={{ fontSize: 24, fontFamily: "Raleway-Bold", top: 48, textAlign: 'center' }}>CM Cosm√©ticos</Text>
            </View>
            <View style={{flex: 1}}>
                <Image
                    style={{width: '100%', height: '100%', flex: 0.5}}
                    source={{
                    uri: 'https://www.shutterstock.com/image-photo/cosmetic-smears-creamy-texture-on-600nw-2170370417.jpg',
                    }}
                />
                <View style={{display: 'flex', flexDirection: 'row', justifyContent: 'space-between', paddingHorizontal: 16, marginTop: 12}}>
                    <Text style={{fontSize: 24, fontFamily: 'Raleway-Regular', color: '#222222'}}>Nome</Text>
                    <Text style={{fontSize: 24, fontFamily: 'Raleway-Medium', color: '#222222'}}>R$ 10,99</Text>
                </View>
                <Text></Text>
            </View>
            <NavBar></NavBar>
        </View>
    )
}
